<strong>Images : </strong>
<style>
  /* Style du conteneur du diaporama */
  .slideshow-container {
    position: relative;
    max-width: 800px; /* Ajustez la largeur du conteneur selon vos besoins */
    margin: 0 auto;
  }

  /* Style des flèches de navigation */
  .prev, .next {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    padding: 10px;
    font-size: 30px;
    cursor: pointer;
  }

  .prev {
    left: 0;
  }

  .next {
    right: 0;
  }
  
  /* Style for the modal */
  #myModal {
    display: none;
    position: fixed;
    z-index: 99999;
    top: 10% !important;
    left: 0;
    max-width: 90%;
    max-height: 80%;
    background-color: #144d6d;
  }

  /* Style for the modal content */
  .modal-content {
    display: block;
    padding: 24px;
    margin: 0 auto;
    max-width: 100%; /* Allow the modal to expand to the image's width */
    max-height: 100%; /* Allow the modal to expand to the image's height */
  }

  /* Style for the close button */
  .close {
    position: absolute;
    top: 15px;
    left: 15px;
    color: white;
    font-size: 40px;
    font-weight: bold;
    cursor: pointer;
  }
</style>

<!-- The Modal -->
<div id="myModal" class="modal">
  <span class="close" onclick="closeModal()">&times;</span>
  <img id="modalImage" class="modal-content">
</div>

<div class="slideshow-container">
  {% for file in files %}
    <div class="mySlides">
      <img src="{{file}}" style="width: 200px; display:block; margin: 0 auto; cursor: pointer;" alt="Image {{ loop.index }}" onclick="openModal('{{file}}')">
    </div>
  {% endfor %}
  {% if files.length > 1 %}
  <!-- Flèches de navigation -->
  <a class="prev" onclick="plusSlides(-1)">&#10094;</a>
  <a class="next" onclick="plusSlides(1)">&#10095;</a>
  {% endif %}
</div>

<script>
  const filesLength = {{files | length}};
  var slideIndex = 0; // Initialize to 0

  // Check if there are images and update slideIndex accordingly
  if (filesLength > 0) {
    slideIndex = 1;
  }

  showSlides(slideIndex);
  
  // Fonction pour avancer ou reculer le diaporama
  function plusSlides(n) {
    showSlides(slideIndex += n);
  }
  
  // Fonction pour afficher les diapositives
  function showSlides(n) {
    var i;
    var slides = document.getElementsByClassName("mySlides");
    
    if (n > filesLength) {
      slideIndex = 1;
    }
    if (n < 1) {
      slideIndex = filesLength;
    }

    for (i = 0; i < filesLength; i++) {
      slides[i].style.display = "none";
    }

    slides[slideIndex - 1].style.display = "block";
  }

  // Open the modal with the clicked image
  function openModal(imageSrc) {
    var modal = document.getElementById("myModal");
    var modalImage = document.getElementById("modalImage");
    modal.style.display = "block";
    modalImage.src = imageSrc;
  }

  // Close the modal
  function closeModal() {
    var modal = document.getElementById("myModal");
    modal.style.display = "none";
  }
</script>

