// Constante qui permet de savoir si on est sur lepetitcoin.org ou lepetitcoin.gogocarto.fr ou tout autre nom de domaine
const domain = window.location.origin;

// ------- À appliquer à toute les pages du site -------

// Sélectionne tous les éléments avec la classe "option-name"
const elements = document.querySelectorAll(".option-name");

// Parcours chaque élément et supprime certains textes
elements.forEach((element) => {
  const text = element.textContent;
  element.innerHTML = text.replace(/(\(Don\)|\(Besoin\))/g, "");
});

// Vérifie si un élément spécifique est affiché pour les administrateurs
if (
  document.querySelector("#listUserActions > li.name.only-for-admin").style
    .display != "none"
) {
  // Charge le script Eruda pour le débogage
  var script = document.createElement("script");
  script.src = "https://cdn.jsdelivr.net/npm/eruda";
  document.body.appendChild(script);
  script.onload = function () {
    eruda.init();
  };
}

// Fonction pour empêcher le comportement par défaut du formulaire
function preventDefaultHandler(event) {
  event.preventDefault();
}

// Fonction pour envoie des mail d'ajout et de modification
function sendMailElement(type) {
  // Récupérer les valeurs des champs
  var title = document.querySelector("#input-name").value;
  var entreprise = document.querySelector("#input-company").value;
  var desactivated = document.querySelector("#field-desactivated2 > div > div > input").value;

  // Charge le script SMTPJS pour l'envoi d'e-mails
  var scriptEmailEdit = document.createElement("script");
  scriptEmailEdit.src = "https://smtpjs.com/v3/smtp.js";
  document.body.appendChild(scriptEmailEdit);

  scriptEmailEdit.onload = function () {
    // Envoie un e-mail de notification
    Email.send({
      Host: "smtp.elasticemail.com",
      Username: "",
      Password: "",
      To: [
        "",
      ],
      From: "",
      Subject: "Fiche " + type,
      Body: "Nom de compte: " + entreprise + "<br>Annonce : " + title + (desactivated != 'Vous souhaitez désactiver votre annonce ?' ? "<br>Raison : " + desactivated : ""),
    });

    console.log("E-mail envoyé avec succès!");
  };
}

// ------- À appliquer à la page acceuil -------
if (window.location.href == domain.toString() + "/") {
  // Crée un nouvel élément div avec des styles personnalisés
  const newDiv = document.createElement("div");
  newDiv.classList.add("content", "valign");
  // Styles personnalisés
  newDiv.style.color = "";
  newDiv.style.fontSize = "17px";
  newDiv.style.textAlign = "justify";
  newDiv.style.padding = "10px";
  newDiv.style.display = "flex";
  newDiv.style.flexWrap = "wrap";
  newDiv.style.justifyContent = "center";
  newDiv.style.marginTop = "0px";
  newDiv.style.marginBottom = "0px";

  // Ajoute du contenu HTML à l'aide de innerHTML
  newDiv.innerHTML = `
      <h2 style="margin:0px;"></h2>
      <p style="margin:0px;" class="front-page-text">Des matériaux et objets inutilisés sont entassés dans un coin de votre bureau ? Mettez-les à disposition gratuitement sur « Le petit coin », plateforme de réemploi et de réutilisation collaborative, dédiée aux entreprises, associations et établissements scolaires du Pays du Calaisis.</p>
      <p style="font-style: italic;font-weight:bold;">Pour faire des déchets des uns, une matière première pour les autres.</p>
      <p style="margin:0px;"></p>
      <a id="btn-directory" style="font-size: 1.7rem; margin:0px;" href="/register/" class="primary-button btn waves-effect waves-light search-bar-btn">S'inscrire</a>`;

  // Sélectionne l'élément parent dans lequel insérer le nouvel élément
  const parentElement = document.querySelector("#home-main-wrapper").parentNode;

  // Insère le nouvel élément après l'élément cible
  document
    .querySelector("#home-logo")
    .insertAdjacentElement("afterend", newDiv);

  // Sélectionne la div cible
  var divCible = document.querySelector(
    "#home-main-container > div:nth-child(7)"
  );

  // Crée un élément div contenant le nouveau contenu
  var nouveauContenu = document.createElement("div");
  nouveauContenu.innerHTML =
    '<br><a href="https://www.sevadec.fr/" target="_blank"><img src="https://www.sevadec.fr/wp-content/uploads/2023/04/logo-lpc.png" alt="logos" style="width: 30%; margin:auto; display:flex;"></a>';

  // Insère le nouveau contenu à côté du bouton
  divCible.appendChild(nouveauContenu);
}

// ------- À appliquer à la page d'ajout d'une annonce -------
if (window.location.href == domain + "/elements/add") {
  // Sélectionne les éléments à masquer
  const elementsToHide = [
    document.querySelector("#page-content > section > div.or-label"),
    document.querySelector("#page-content > section > form"),
    document.querySelector("#page-content > section > center"),
    document.querySelector("#element-form-content > section:nth-child(3)"),
    document.querySelector("#field-company"),
  ];

  // Masque les éléments
  elementsToHide.forEach((element) => {
    if (element) {
      element.style.display = "none";
    }
  });

  // Sélectionne le texte du bouton de déconnexion
  const buttonText = document.querySelector("#btn-logout > span").textContent;

  // Sélectionne l'élément #input-company
  const inputCompany = document.querySelector("#input-company");

  // Remplit l'élément #input-company avec le texte du bouton de déconnexion
  inputCompany.value = buttonText;

  // Ajoute l'événement de clic au bouton
  buttonElement.addEventListener("click", handleButtonClick);

  var boutonAdd1 = document.getElementById("element-form-submit-btn");
  var boutonAdd2 = document.querySelector("#section-validate-and-send > center > button:nth-child(2)");

  // Ajoutez un écouteur d'événement 
  boutonAdd1.addEventListener("click", function (event) {
    event.preventDefault(event);
    sendMailElement("ajoutée")
  });

  // Ajoutez un écouteur d'événement 
  boutonAdd2.addEventListener("click", function (event) {
    event.preventDefault(event);
    sendMailElement("ajoutée")
  });

}

// ------- À appliquer à la page s'incrire -------
if (window.location.href == domain + "/register/") {
  // Crée un nouvel élément div pour les conditions générales d'utilisation
  const newDivCgu = document.createElement("div");
  newDivCgu.classList.add("content", "valign");
  newDivCgu.style.color = "";
  newDivCgu.style.fontSize = "17px";
  newDivCgu.style.textAlign = "justify";
  newDivCgu.style.padding = "10px";

  // Ajoute du contenu HTML pour les conditions générales d'utilisation
  newDivCgu.innerHTML = `
    <p>En créant un compte, vous confirmez avoir pris connaissance et accepté les <a class="about-title-item" id="modal2" onclick="$('#popup-2').openModal()" style="cursor: pointer;">conditions générales d'utilisation.</a></p>
  `;

  // Sélectionne l'élément parent dans lequel insérer le nouvel élément
  const parentElementRegister = document.querySelector("#page-container").parentNode;

  // Insère le nouvel élément après l'élément cible
  document
    .querySelector("#page-content > form > section:nth-child(3) > div.form-actions")
    .insertAdjacentElement("afterend", newDivCgu);

  // Modification pour l'obligation des CGU
  document.querySelector(".btn-ok").textContent = "j'accepte";

  // Sélectionne l'élément <label> par son attribut for
  var labelElement = document.querySelector('label[for="input-username"]');

  // // Sélectionne l'élément <input> par son ID
  // var inputElement = document.getElementById("input-username");

  // // Vérifie si l'élément <label> et l'élément <input> ont été trouvés
  // if (labelElement && inputElement) {
  //   // Modifie le texte de l'étiquette
  //   labelElement.textContent = "Nom de la société";

  //   // Ajoute un attribut placeholder à l'élément <input>
  //   inputElement.placeholder = "(Exemple : Ma Société)";
  // }

  // Ajouter un nouvel input pour le numéro d'identification
  const newDivIdentification = document.createElement("div");
  newDivIdentification.classList.add("input-field", "col", "s12");
  newDivIdentification.innerHTML = `
  <i class="material-icons far fa-building prefix"></i>
  <input type="text" id="input-identification">
  <label for="input-identification" class="gogo-form-label">Identification (Nom de la société/association, Siret, RNA...) <a href="https://annuaire-entreprises.data.gouv.fr/" target="_blank" title="Retrouvez votre société / association / établissement scolaire sur annuaire-entreprises.data.gouv.fr"><sup style="font-size: 19px;
  color: #26A69A;">?</sup></a></label>
  <div class="error" id="error-message"></div>
  
`;

  // Ajouter le nouvel élément à votre formulaire
  document.body.appendChild(newDivIdentification);




  // Select the container after which the new input should be inserted
  const emailContainer = document.querySelector('.input-field.col.s12');

  // Insert the newDivIdentification after the email input container
  emailContainer.parentNode.insertBefore(newDivIdentification, emailContainer.nextSibling);

  // Create and insert <div class="error"></div> above newDivIdentification
  const errorContainer = document.createElement("div");
  errorContainer.classList.add("error");
  newDivIdentification.parentNode.insertBefore(errorContainer, newDivIdentification);

  // Envoie un e-mail de notification
  document.querySelector('input[type="submit"]').addEventListener("click", async function (event) {
    // Empêcher le comportement par défaut du bouton (soumission du formulaire)
    event.preventDefault();

    // Récupérer les valeurs des champs
    var username = document.querySelector("#input-username").value;
    var email = document.querySelector("#input-email").value;
    var identification = document.querySelector("#input-identification").value;

    // Vérifier l'existence de l'entreprise
    try {
      const apiUrl = `https://recherche-entreprises.api.gouv.fr/search?q=${identification}`;
      const response = await fetch(apiUrl);

      if (!response.ok) {
        throw new Error(`Erreur lors de la requête API : ${response.status}`);
      }

      const data = await response.json();

      if (data.results && data.results.length > 0) {
        // L'entreprise existe, renvoyer son nom
        const nomEntreprise = data.results[0].nom_complet;
        // console.log(`L'entreprise existe : ${nomEntreprise}`);
        const termeEncoded = encodeURIComponent(identification);
        const resultatRecherche = `https://annuaire-entreprises.data.gouv.fr/rechercher?terme=${termeEncoded}&cp_dep_label=Pas-de-Calais+%2862%29&cp_dep_type=dep&cp_dep=62&fn=&n=&dmin=&dmax=&type=&label=&etat=&sap=&naf=&nature_juridique=&tranche_effectif_salarie=&categorie_entreprise=`;


        // Charge le script SMTPJS pour l'envoi d'e-mails
        var scriptEmail = document.createElement("script");
        scriptEmail.src = "https://smtpjs.com/v3/smtp.js";
        document.body.appendChild(scriptEmail);

        scriptEmail.onload = function () {
          // Envoie un e-mail de notification
          Email.send({
            Host: "smtp.elasticemail.com",
            Username: "",
            Password: "",
            To: [
              "",
            ],
            From: "",
            Subject: "Nouvel utilisateur",
            Body: "Nom de compte: " + username + "<br>Adresse mail: " + email + "<br>Numéro d'identification: " + identification + "<br>Lien : " + resultatRecherche + "<br>Nom de l'entreprise: " + nomEntreprise,
          });

          // Afficher la confirmation une fois l'e-mail envoyé
          console.log("E-mail envoyé avec succès!");

          // Restaurer le comportement normal du formulaire
          document.querySelector('form').submit();
        };
      } else {
        // Afficher un message d'erreur dans l'élément spécifié
        document.querySelector("#page-content > form > section:nth-child(1) > div:nth-child(4)").innerHTML = "<div class='error'><ul><li>Veuillez renseigner un numéro d'identification valide</li></ul></div>";
      }
    } catch (error) {
      console.error(`Erreur : ${error.message}`);
    }
  });
}

// ------- À appliquer à la page édition des annonces -------
if (location.href.startsWith(domain + "/elements/edit/")) {


  var boutonEdit1 = document.getElementById("element-form-submit-btn");
  var boutonEdit2 = document.querySelector("#section-validate-and-send > center > button:nth-child(2)");

  // Ajoutez un écouteur d'événement 
  boutonEdit1.addEventListener("click", function (event) {
    event.preventDefault(event);
    sendMailElement("modifiée")
  });

  // Ajoutez un écouteur d'événement 
  boutonEdit2.addEventListener("click", function (event) {
    event.preventDefault(event);
    sendMailElement("modifiée")
  });

  // Ajoute l'événement de clic au bouton
  buttonElement.addEventListener("click", handleButtonClick);

  // Ajoute un écouteur d'événement "click" pour détecter les clics sur l'élément d'entrée
  document
    .querySelector("#field-desactivated2")
    .addEventListener("click", function () {
      // Affiche un message de débogage
      document.querySelector("#send_mail").value = "on";

      // Met à jour la valeur de l'élément #admin-message avec la nouvelle valeur
      document.querySelector("#admin-message").value =
        document.querySelector("#field-desactivated2 input").value !== ""
          ? "Je souhaite désactiver mon annonce : " +
          document.querySelector("#field-desactivated2 input").value
          : "";
    });
}

// ------- À appliquer à la page qui apparait à la fin d'une inscription -------
if (window.location.href == domain + "/register/check-email") {

  // Ajoute du texte supplémentaire à la confirmation
  const confirmationText = document.querySelector(
    "#page-content > section > p"
  );

  if (confirmationText) {
    confirmationText.innerHTML +=
      "<br>Un second mail vous sera ensuite envoyé après vérification d'un administrateur. Si vous n'avez rien reçu d'ici 72h, vérifiez vos spams (ou junkmail).";
  }
}
